INSERT INTO `posts` (`id`, `type`, `date`, `title`, `writer`, `short_description`, `video_link`, `description`, `head_line`, `published`, `featured`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'events', '2023-08-03', 'إنطلاق الدورة التدريبية بعنوان (فنون الخياطة)', NULL, NULL, NULL, '<p>‏ولله الحمد تم بالأمس إنطلاق الدورة التدريبية بعنوان (فنون الخياطة) لمشروع أنامل دعم الممنوح من برنامج أهالينا للاستثمار الاجتماعي لمستفيدي جمعية دعم تحت رعاية من مؤسسة الملك خالد</p>', 0, 0, 0, '2023-08-03 10:58:12', '2023-10-11 12:53:03', NULL),
(2, 'tkremat', '2023-04-11', 'تكريم جمعية دعم في حفل المشاركين بأعمال التسكين', NULL, 'تكريم جمعية دعم في حفل المشاركين بأعمال التسكين', NULL, '<p>&nbsp;تكريم جمعية دعم في حفل المشاركين بأعمال التسكين</p><p>&nbsp;‏قام صاحب السمو الملكي الامير بدر بن سلطان بن عبدالعزيز نائب أمير منطقة مكة المكرمة ليلة البارحه في مقر &nbsp;الامارة بجده وبحضور معالي نائب وزير الاسكان ومعالي امين محافظة جدة بتكريم الجمعيات المشاركة بالاسكان بالتعاون مع وزارة الاسكان ومنهم جمعية دعم لرعاية الارامل والمطلقات بمكة</p>', 0, 1, 0, '2023-09-12 02:49:02', '2023-09-15 05:55:13', NULL),
(3, 'news', '2023-09-11', 'مبادرة انشر الخير', NULL, NULL, NULL, '<p>تشرفت الجمعية بزيارة المدير التنفيذي للمنصة الوطنية للتبرعات \"تبرع\" أ. صالح العمري لتقديم الدعم من⁩ ⁧<a href=\"https://twitter.com/hashtag/%D9%85%D8%A7%D9%83%D8%AF%D9%88%D9%86%D8%A7%D9%84%D8%AF%D8%B2_%D8%A7%D9%84%D8%B3%D8%B9%D9%88%D8%AF%D9%8A%D8%A9?src=hashtag_click\">#ماكدونالدز_السعودية</a>⁩ وهي اجهزة كهربائية ضمن مبادرة انشر الخير</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:35:59', '2023-09-17 01:24:53', NULL),
(4, 'news', '2023-09-11', 'مشروع الكسب الكريم', NULL, NULL, NULL, '<p>انطلاق مشروع الكسب الكريم في اول ايام التدريب على مهارات سوق العمل والمنتهي بالتوظيف مع الجهات الشريكة والمدعوم من مؤسسة عبدالعزيز الجميح</p><p><br>&nbsp;</p>', 1, 1, 1, '2023-09-15 05:38:23', '2023-09-15 05:55:01', NULL),
(5, 'news', '2023-08-31', 'زياره مؤسسة غروس', NULL, NULL, NULL, '<p>تشرفت جمعية <a href=\"https://twitter.com/hashtag/%D8%AF%D8%B9%D9%85?src=hashtag_click\">#دعم</a> بزيارة ممثل مؤسسة محمد ابراهيم السبيعي <a href=\"https://twitter.com/hashtag/%D8%BA%D8%B1%D9%88%D8%B3?src=hashtag_click\">#غروس</a> الأستاذة / نوال المشيبه، وذلك للاطلاع على برامج الجمعية ومبادراتها، وبحث سبل التعاون بين الجهتين</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:40:05', '2023-09-17 01:24:59', NULL),
(6, 'news', '2023-08-31', 'زيارة جمعية ترميم للتنمية', NULL, NULL, NULL, '<p>استضافة جمعية <a href=\"https://twitter.com/hashtag/%D8%AF%D8%B9%D9%85_%D9%84%D8%B1%D8%B9%D8%A7%D9%8A%D8%A9_%D8%A7%D9%84%D9%85%D8%B7%D9%84%D9%82%D8%A7%D8%AA_%D9%88%D8%A7%D9%84%D8%A7%D8%B1%D8%A7%D9%85%D9%84_%D9%88%D8%A7%D8%A8%D9%86%D8%A7%D8%A6%D9%87%D9%86?src=hashtag_click\">#دعم_لرعاية_المطلقات_والارامل_وابنائهن</a> جمعية <a href=\"https://twitter.com/hashtag/%D8%AA%D8%B1%D9%85%D9%8A%D9%85_%D9%84%D9%84%D8%AA%D9%86%D9%85%D9%8A%D8%A9?src=hashtag_click\">#ترميم_للتنمية</a>⁩ وقدمت أ/ وفاء العزب المديرة التنفيذية لجمعية دعم شكرها لإدارة وفريق ترميم على جهودهم لإنجاح الشراكة بما يعود بالنفع على المستفيدات من الأرامل والمطلقات وابنائهن.</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:41:35', '2023-09-17 01:25:04', NULL),
(7, 'news', '2023-08-23', 'مجلس الجمعيات الاهلية بمكة المكرمة', NULL, NULL, NULL, '<p>بدعوة من ⁧<a href=\"https://twitter.com/hashtag/%D9%85%D8%AC%D9%84%D8%B3_%D8%A7%D9%84%D8%AC%D9%85%D8%B9%D9%8A%D8%A7%D8%AA_%D8%A7%D9%84%D8%A7%D9%87%D9%84%D9%8A%D8%A9?src=hashtag_click\">#مجلس_الجمعيات_الاهلية</a>⁩ حضور ⁧<a href=\"https://twitter.com/hashtag/%D8%AC%D9%85%D8%B9%D9%8A%D8%A9?src=hashtag_click\">#جمعية</a> دعم لرعاية المطلقات والارامل وابنائهن - ⁩ اللقاء التشاوري لجمعيات الأسر المنتجة بمدينة الطيبات بجدة</p><p>&nbsp;⁧<a href=\"https://twitter.com/hashtag/%D9%85%D8%AA%D8%AD%D9%81_%D8%B9%D8%A8%D8%AF%D8%A7%D9%84%D8%B1%D8%A4%D9%88%D9%81_%D8%AC%D9%85%D9%8A%D9%84?src=hashtag_click\">#متحف_عبدالرؤوف_جميل</a>⁩</p>', 0, 1, 1, '2023-09-15 05:43:15', '2023-09-17 01:25:10', NULL),
(8, 'news', '2023-08-20', 'تكريم المتطوعين في اليوم العالمى للعمل الانساني', NULL, NULL, NULL, '<p>في <a href=\"https://twitter.com/hashtag/%D8%A7%D9%84%D9%8A%D9%88%D9%85_%D8%A7%D9%84%D8%B9%D8%A7%D9%84%D9%85%D9%8A_%D9%84%D9%84%D8%B9%D9%85%D9%84_%D8%A7%D9%84%D8%A5%D9%86%D8%B3%D8%A7%D9%86%D9%8A?src=hashtag_click\">#اليوم_العالمي_للعمل_الإنساني</a> كرمت جمعية دعم منسوبيها نظرا لما يقدمونه من مجهودات في العمل الانساني<br>&nbsp;</p>', 0, 0, 1, '2023-09-15 05:44:49', '2023-10-25 01:55:05', NULL),
(9, 'events', '2023-08-10', 'لقاء تثقيفي بعنوان نشر ثقافة العمل الحر', NULL, NULL, NULL, '<p>أقامت جمعية دعم لرعاية المطلقات والأرامل وابنائهن لقاء تثقيفي عن نشر ثقافة العمل الحر لمستفيدات الجمعية والذي يهدف إلى تنمية معارف المستفيدات عن أهمية العمل الحر وعوامل تحقيق النجاح في العمل الحر وبقيادة الأستاذة: فاتن علي عابد</p>', 1, 1, 1, '2023-09-15 05:46:36', '2023-10-25 01:55:00', NULL),
(10, 'news', '2023-08-02', 'العطاء باحسان', NULL, NULL, NULL, '<p>&nbsp;ضمن مشروع ⁧<a href=\"https://twitter.com/hashtag/%D8%A7%D9%84%D8%A3%D8%AC%D9%87%D8%B2%D8%A9_%D8%A7%D9%84%D9%83%D9%87%D8%B1%D8%A8%D8%A7%D8%A6%D9%8A%D8%A9?src=hashtag_click\">#الأجهزة_الكهربائية</a>⁩ للمحتاجين وبدعم سخي من ⁧<a href=\"https://twitter.com/hashtag/%D9%85%D9%86%D8%B5%D8%A9_%D8%A5%D8%AD%D8%B3%D8%A7%D9%86?src=hashtag_click\">#منصة_إحسان</a>⁩ ، جمعية دعم لرعاية المطلقات والارامل وابنائهن تقوم بتوزيع 60 جهاز تكييف ، سائلين الله أن يجزي المتبرعين والقائمين على ⁦&nbsp;</p><p>⁩ منصة احسان والداعمين لها خير الجزاء&nbsp;</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 05:49:57', '2023-10-30 20:30:36', '2023-10-30 20:30:36'),
(11, 'news', '2023-05-19', 'مستفيداتنا الكرام نعتذر عن توقف خدمة استقبال الطلبات الجديدة للتسجيل في وحدة الاسكان التنموي', NULL, NULL, NULL, '<p>مستفيداتنا الكرام نعتذر عن توقف خدمة استقبال الطلبات الجديدة للتسجيل في وحدة الاسكان التنموي وذلك حتى يتم الانتهاء من تسكين المنتقلين من الاحياء العشوائية المزالة شاكرين لكم ثقتكم وتفهمكم نعتز بخدمتكم</p><p><br>&nbsp;</p>', 0, 0, 0, '2023-09-15 05:52:10', '2023-10-10 00:04:25', NULL),
(12, 'events', '2023-06-06', 'مشاركة جمعية دعم ملتقى بعنوان  المرأة السعودية تمكين وتميز', NULL, NULL, NULL, '<p>شاركت رئيسة مجلس ادارة جمعية دعم لرعاية المطلقات والارامل وابنائهن الاستاذه نورة بنت عبدالعزيز آل الشيخ في الملتقى بعنوان <a href=\"https://twitter.com/hashtag/%D8%A7%D9%84%D9%85%D8%B1%D8%A3%D8%A9_%D8%A7%D9%84%D8%B3%D8%B9%D9%88%D8%AF%D9%8A%D8%A9_%D8%AA%D9%85%D9%83%D9%8A%D9%86_%D9%88%D8%AA%D9%85%D9%8A%D8%B2?src=hashtag_click\">#المرأة_السعودية_تمكين_وتميز</a> وذلك بالتعاون مع كرسي جامعة جدة للدعم المجتمعي ولجنة الاسرة والتنمية التابعة لمنطقة مكة المكرمة.&nbsp;<br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:00:39', '2023-09-15 06:00:39', NULL),
(13, 'news', '2023-06-01', 'تعاون جمعيه دعم مع شركة اكستريم بلاي', NULL, NULL, NULL, '<p>قامت جمعية دعم بالتعاون مع شركة الطاقة والحماس (اكستريم بلاي) برحلة لأطفال الجمعية بمناسبة يوم الطفولة العالمي ومنحهم صالة الألعاب (ترامبولين) مع تقديم وجبة غداء لهم وقضاء يوم ممتع وإضفاء البهجه والسرور على اطفال الجمعية</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:02:28', '2023-09-15 06:02:28', NULL),
(14, 'news', '2023-05-30', 'مشاركة جمعية دعم جولةتطويرالقطاع  للمركز الوطنى بمنطقة مكة المكرمة', NULL, NULL, NULL, '<p><a href=\"https://twitter.com/hashtag/%D9%8A%D8%AD%D8%AF%D8%AB_%D8%A7%D9%84%D8%A7%D9%86?src=hashtag_click\">#</a> مشاركة جمعية دعم <a href=\"https://twitter.com/hashtag/%D8%AC%D9%88%D9%84%D8%A9_%D8%AA%D8%B7%D9%88%D9%8A%D8%B1_%D8%A7%D9%84%D9%82%D8%B7%D8%A7%D8%B9?src=hashtag_click\">#جولة_تطوير_القطاع</a> للمركز الوطنى بمنطقة مكة المكرمة</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:04:07', '2023-09-15 06:04:07', NULL),
(15, 'events', '2023-05-28', 'حضور فعاليات معرض (إينا - IENA) الدولي لـ ⁧#القطاع_غير_الربحي⁩ والأوقاف', NULL, NULL, NULL, '<p><a href=\"https://twitter.com/hashtag/%D9%8A%D8%AD%D8%AF%D8%AB_%D8%A7%D9%84%D8%A2%D9%86?src=hashtag_click\">#</a>حضور أ. نورة العتيبي ممثله لجمعية دعم للجلسة حول إسهامات المنظمات الدولية في التنمية المستدامة، ضمن فعاليات معرض&nbsp;</p><p>(إينا - IENA)</p><p>&nbsp;الدولي لـ ⁧<a href=\"https://twitter.com/hashtag/%D8%A7%D9%84%D9%82%D8%B7%D8%A7%D8%B9_%D8%BA%D9%8A%D8%B1_%D8%A7%D9%84%D8%B1%D8%A8%D8%AD%D9%8A?src=hashtag_click\">#القطاع_غير_الربحي</a>⁩ والأوقاف</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:06:17', '2023-09-15 06:06:17', NULL),
(16, 'events', '2023-04-16', 'مشاركة جمعية دعم في حملة قد الثقه', NULL, NULL, NULL, '<p>من ضمن مبادرة ابشرِ بالخير في شهر الخير ) التي اطلقتها جمعية دعم لمستفيداتها توفير الاجهزة الكهربائيه لمتضررات ازالة الاحياء العشوائية واللاتي تم تسكينهن في الواحدات السكنية التابعه لوزارة الاسكان التنموي&nbsp;<br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:09:10', '2023-09-15 06:09:10', NULL),
(17, 'tkremat', '2023-04-09', 'تكريم جمعية دعم من مكتب الاستاذة بيان زهران للمحاماة', NULL, NULL, NULL, '<p><a href=\"https://twitter.com/hashtag/%D9%8A%D8%AD%D8%AF%D8%AB?src=hashtag_click\">#</a>تم تكريم جمعية دعم لرعاية المطلقات والارامل وابنائهن من مكتب الاستاذة بيان زهران للمحاماة والاستشارات القانونية</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:11:18', '2023-09-15 06:11:18', NULL),
(18, 'events', '2023-03-09', 'احتفال يوم المرأة العالمي', NULL, NULL, NULL, '<p>تم الاحتفال بيوم المرأة العالمي بجمعية دعم لرعاية المطلقات والارامل وابنائهن بحضور مستفيدات الجمعية وقدتمت استضافة الاستاذه شجن السليماني تحدثت عن المشاريع الصغيرة وربادة الاعمال ومستشارة المظهر نجلاء قشعر والاستاذه منى العبدلي وتحدثت عن الالوان وتأثيرها على نفسية المراة</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:18:10', '2023-09-15 06:18:10', NULL),
(19, 'news', '2023-03-08', 'حضور الاستاذة وفاء العزب ورشة العمل الوطنية لمنظمات المجتمع المدني', NULL, NULL, NULL, '<p>حضور المديرة التنفيذية لجمعية دعم الاستاذة وفاء العزب ضمن حضور القطاع الغير ربحي التابع للمركز الوطني ورشة العمل الوطنية لمنظمات المجتمع المدني في المملكة العربية السعودية</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:21:00', '2023-09-15 06:21:00', NULL),
(20, 'news', '2023-03-07', 'شكر وتقدير لمؤسسة اسماعيل ابو داوود', NULL, NULL, NULL, '<p>تتقدم جمعية دعم بخالص الشكر والتقدير على الرعاية الكريمة من مؤسسة اسماعيل على ابو داوود حيث تم دعم الجمعية بالسلال الغذائية الرمضانية وكسوة عيد</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:23:13', '2023-09-15 06:23:13', NULL),
(21, 'events', '2023-03-01', 'بازار دعم رمادية', NULL, NULL, NULL, '<p>تتشرف جمعية دعم بدعوتكم لحضور بازار الجمعية لدعم الأسر المنتجة والمشاريع الصغيرة&nbsp;<br>&nbsp;</p>', 0, 1, 0, '2023-09-15 06:23:57', '2023-09-15 06:23:57', NULL),
(22, 'tkremat', '2022-07-12', 'تكريم جمعيه دعم فى الملتقي التطوعى السنوى بالرياض', NULL, NULL, NULL, '<p>برعاية كريمة من معالي وزير الموارد البشرية والتنمية الإجتماعية المهندس أحمد بن سليمان الراجحي ، تم تكريم الجمعية لتأسيسها وحدة التطوع وفق المعاير الوطني “إدامة” في ⁧‫ملتقى التطوع السنوي‬⁩ بالرياض.&nbsp;<br>&nbsp;</p>', 0, 1, 0, '2023-09-15 07:39:20', '2023-09-15 07:39:20', NULL),
(23, 'tkremat', '2021-09-12', 'شكر الجمعية لمعالي امين محافظة جده الأستاذ / صالح الترك', NULL, NULL, NULL, '<p>‏تتقدم جمعية دعم لرعاية المطلقات و الارامل و ابنائهن بخالص الشكر و التقدير لمعالي امين محافظة جده الاستاذ / صالح التركي لتكريمه جمعية دعم بمناسبة اليوم العالمي للتطوع</p>', 0, 1, 0, '2023-09-15 07:40:58', '2023-09-15 07:40:58', NULL),
(24, 'news', '2021-10-12', 'زيارة كريمة من جمعية البر الخيرية', NULL, NULL, NULL, '<p>زيارة كريمة من جمعية البر الخيرية بمحافظة خليص حيث تأتي هذه الزيارة تمهيداً لعقد شراكة تعاونية بين الطرفين وتشرفنا خلال الزيارة باستلام شهادة شكر وتقدير مقدمة من الجمعية</p>', 0, 1, 0, '2023-09-15 07:42:18', '2023-09-15 07:42:18', NULL),
(25, 'news', '2021-09-21', 'اقامة احتفالية اليوم الوطني 91', NULL, NULL, NULL, '<p>احتفلت جمعية دعم يوم الثلاثاء باليوم الوطني الـ 91 للمملكة، بحضور مستفيدات الجمعية وابنائهن ومنسوبات الجمعية وذلك في مقر الجمعية</p>', 0, 0, 0, '2023-09-15 07:43:27', '2023-10-11 12:46:19', NULL),
(26, 'events', '2021-08-23', 'انطلاق ورشة عمل ادارة المشاريع التنموية', NULL, NULL, NULL, '<p>انطلاق ورشة عمل ادارة المشاريع التنموية وهي احدى الورش في مسار تطوير القيادات ضمن مشروع إمكان</p>', 0, 0, 0, '2023-09-15 07:44:49', '2023-10-11 12:46:26', NULL),
(27, 'events', '2021-12-21', 'انطلاق برنامج أنامل دعم للخياطة', NULL, NULL, NULL, '<p>انطلاق برنامج أنامل دعم للخياطة</p><p>لتأهيل المستفيدات من خلال برنامج تدريبي متميز في اساسيات الخياطة المصنعية وخطوط الإنتاج لتنمية مهارات المستفيدات</p><p>.إيجاد فرص زيادة الدخل للمتدربات من خلال انتاج المنتوجات بقيمة مدفوعة من خلال ربطهن بمعامل خياطة</p>', 0, 0, 0, '2023-09-15 07:46:50', '2023-10-11 12:46:30', NULL),
(28, 'events', '2021-08-09', 'اقامة أمسية العودة للمدارس', NULL, NULL, NULL, '<p>أقامت جمعية دعم أمسية العودة للمدارس والاستعداد النفسي لأبناء &nbsp;مع المدرب الأستاذ / أشرف الحارثي</p>', 0, 0, 0, '2023-09-15 07:48:30', '2023-10-11 12:46:36', NULL),
(29, 'events', '2021-10-28', 'اللقاء التعريفي لمشروع ( انامل دعم )', NULL, NULL, NULL, '<p>حدث اليوم اللقاء التعريفي لمشروع ( انامل دعم ) لفنون الخياطة&nbsp; تحت رعاية مؤسسة الملك خالد</p><p>&nbsp;‎@KKFoundation</p><p>&nbsp; بحضور مديرة البرامج والانشطة أ/ هناء ناقرو&nbsp;ومديرة المشروع أ / شيماء الثقفي</p>', 0, 0, 0, '2023-09-15 07:49:59', '2023-10-11 12:46:40', NULL),
(30, 'tkremat', '2022-02-25', 'زيارة وتكريم وكيل إمارة منطقة مكة المكرمة معالي الدكتور مشبب القحطاني لجمعية دعم', NULL, NULL, NULL, '<p>وكيل إمارة منطقة مكة المكرمة معالي الدكتور مشبب القحطاني يزور معرض جمعية دعم لرعاية المطلقات والارامل وابنائهن فى مرتمر التحول التفنى&nbsp;</p><p>&nbsp; حيث تم تكريم جمعية دعم لرعاية المطلقات والارامل وابنائهن من قبل وكيل الاماره الاستاذ مشبب القحطاني في مؤتمر التحول الرقمي بمكة&nbsp;</p>', 0, 1, 0, '2023-09-15 07:51:07', '2023-09-15 07:51:07', NULL),
(31, 'events', '2022-02-24', 'فعاليات مؤتمر التحول الرقمي بمكة المكرمة', NULL, NULL, NULL, '<p><br>&nbsp; فعاليات مؤتمر التحول الرقمي بمكة المكرمة والذي انطلق يوم الاربعاء برعاية سمو أمير منطقة مكة المكرمة الأميرخالد الفيصل&nbsp;</p><p>تشارك جمعية دعم&nbsp;لرعاية المطلقات والأرامل وأبنائهن في المعرض المصاحب للمؤتمر بأبرز مشاريعه</p>', 0, 1, 0, '2023-09-15 07:52:16', '2023-09-15 07:52:16', NULL),
(32, 'events', '2022-02-17', 'فعاليات اليوم العالمي لسرطان الطفل', NULL, NULL, NULL, '<p>شاركت جمعية دعم لرعاية المطلقات والارامل وابنائهن مركز التنمية الاجتماعية بجدة تفعيل اليوم العالمي لسرطان الطفل بالتعاون مع</p><p><a href=\"https://twitter.com/SanedSocial\">@SanedSocial</a></p><p>لمرضى سرطان الأطفال وقد سلم وكيل وزارة الموارد البشرية والتنمية الاجتماعية ومدير مركز التنمية بجدة شهادات شكر وتقدير للجمعيات المشاركة</p>', 0, 1, 0, '2023-09-15 07:54:00', '2023-09-15 07:54:00', NULL),
(33, 'news', '2022-02-10', 'زيارة الوحدة الاشرافيه بوكالة الإسكان التنموي والمشاركة المجتمعية', NULL, NULL, NULL, '<p><br>تشرفت <a href=\"\">جمعية دعم</a> بزيارة الوحدة الاشرافيه بوكالة الإسكان التنموي والمشاركة المجتمعية لتقييم قدرات وخدمات الجمعية</p>', 0, 1, 0, '2023-09-15 07:55:53', '2023-09-15 07:55:53', NULL),
(34, 'events', '2022-02-08', 'مشاركة دعم في مشروع بناء قدرات الجمعيات الاهلية ودعم تشغيلها', NULL, NULL, NULL, '<p>‏‎تمت مشاركة جمعية دعم لرعاية الارامل و المطلقات بمنطقة مكة المكرمة في ورشة تقييم وتصنيف الجمعيات ضمن مشروع بناء قدرات الجمعيات الأهلية ودعم تشغيلها بإشراف الوحده الاشرافيه لتطوير القطاع الغير ربحي بوكالة الاسكان التنموي والمشاركة المجتمعية&nbsp;</p><p>‎@dev_housing ‎@saudimomra https://t.co/o9qkRPRRVN‎‎‎</p>', 0, 1, 0, '2023-09-15 07:57:51', '2023-09-15 07:57:51', NULL),
(35, 'news', '2022-06-15', 'توقيع اتفاقية تعاون مع معهد أرياد للتدريب', NULL, NULL, NULL, '<p>تم توقيع اتفاقية تعاون مع معهد أرياد للتدريب وذلك لتنفيذ مشروع مسارات دعم للتأهيل والتوظيف لفئة المطلقات والأرامل ستنطلق اولى برامجه الاحد القادم يمثل جمعية دعم المدير التنفيذي الاستاذة وفاء العزب و من المعهد المدير العام الاستاذة نوف القحطاني سائلين الله التوفيق والنجاح</p>', 0, 1, 0, '2023-09-15 07:59:22', '2023-09-15 07:59:22', NULL),
(36, 'news', '2022-09-04', 'انطلاق الحملة الوطنية للعمل الخيري', NULL, NULL, NULL, '<p>بدعم من خادم الحرمين الشريفين&nbsp; ‏نسعد اليوم بإنطلاق الحملة الوطنية للعمل الخيري</p><p>‎ في نسختها الثانية من خلال المنصة الوطنية للعمل الخيري \"إحسان\"، التي تساهم بدعم مشاريع الجمعيات وتحقيق مستهدفاتها التنموية</p>', 0, 1, 0, '2023-09-15 08:00:44', '2023-09-15 08:00:44', NULL),
(37, 'events', '2022-04-02', 'بدء حملة قد الثقه', NULL, NULL, NULL, '<p>&nbsp; نبدأ بعون الله فى اطلاق حملة قد الثقه&nbsp; للسنة الثالثة على التوالي بتنظيم من ‏مؤسسة الملك خالد&nbsp;<br>‎@KKFoundation</p><p>احتفاءً بالشهر الكريم الذي تنشط فيه أعمال المنظمات غير الربحية في خدمة المجتمع ويتفاعل معه الأفراد والشركات بالدعم والتطوع والعطاء، فإننا بصدد إطلاق حملة قد الثقة للسنة الثالثة على التوالي على مواقع التواصل الاجتماعي؛ وهي حملة توعوية تهدف إلى تقديم صورة إيجابية عن القطاع غير الربحي السعودي ودوره التنموي. وتهدف الحملة إلى تعزيز ثقة المجتمع بالقطاع غير الربحي وتشجيع المشاركة والتطوع والتبرع والعضوية في المنظمات غير الربحية. كما تهدف الحملة إبراز دور القطاع غير الربحي السعودي كشريك تنموي موثوق يلتزم بأعلى معايير الحوكمة والمساءلة والشفافية ويساهم بشكل مباشر في تحقيق رؤية المملكة 2030.</p><p><br>&nbsp;</p>', 0, 1, 0, '2023-09-15 08:02:52', '2023-09-15 08:02:52', NULL),
(38, 'news', '2022-03-30', 'شكر وعرفان لفريق رواء التطوعى', NULL, NULL, NULL, '<p>‏يسر إدارة جمعية دعم أن تتقدم بخالص الشكر والعرفان لفريق رواء التطوعي</p><p>@RewTeam</p><p>التابع لحي الروضة بجده على مجهوداتهم الطيبة لمبادرتهم &nbsp;بعمل دورة تدريبية للأطفال فى صناعة الكونكريت والصابون سائلين الله أن يجزيهم خير الجزاء<br>والجمعية ترحب بكل المبادرات المماثله والموجهه للمرأةوالطفل</p>', 0, 1, 0, '2023-09-15 08:03:57', '2023-09-15 08:03:57', NULL),
(39, 'news', '2022-03-01', 'مشروع الاجهزة الكهربائية للمعسرين بالتعاون مع منصة احسان', NULL, NULL, NULL, '<p>﴾&nbsp;قال تعالى: ﴿وَأَحْسِنُوا إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ</p><p>باحسانكم وتبرعكم ساهمتم في توفير اجهزة كهربائية لمستفيداتنا من الارامل والمطلقات&nbsp;بدعم من #منصة_إحسان&nbsp;&nbsp;</p><p>لمشروع الاجهزة الكهربائية للمعسرين</p><p><br>شكراً لكريم عطاءكم وجزاكم الله خيرًا</p>', 0, 1, 0, '2023-09-15 08:04:53', '2023-09-15 08:04:53', NULL),
(40, 'news', '2022-08-18', '١٣٤ قانونيًّا سعوديًّا يستعدون لدخول \"جينيس\" بـ\"الأقلام العدلية\"', NULL, NULL, NULL, '<p>أن كتاب (الأقلام العدلية في المقالات القانونية) يُعدُّ إنجازًا يُسجَّل للعاملين بمهنة المحاماة في المملكة العربية السعودية؛ كونه يحمل مبادرتَيْن نبيلتَيْن، هما توعية وتثقيف المجتمع قانونيًّا، ودعم الأرامل والمطلقات وأسرهن من خلال تخصيص دخله لجمعية \"دعم\" الخيرية كأول وقف علمي قانوني، فضلاً عن كونه مرشحًا لدخول موسوعة جينيس للأرقام القياسية كإنجاز سعودي</p>', 0, 1, 0, '2023-09-15 08:06:38', '2023-09-15 08:06:38', NULL),
(41, 'news', '2022-08-12', 'شراكة مع جمعية حماية الاسرة ولوريال باريس', NULL, NULL, NULL, '<p>تعقد جمعية حماية الأسرة بالشراكة مع لوريال باريس مع جمعية دعم لرعاية المطلقات والأرامل وابنائهن برنامج للتصدي للمضايقات فى الأماكن العامة تحت عنوان ستاند اب</p>', 0, 1, 0, '2023-09-15 08:07:45', '2023-09-15 08:07:45', NULL),
(42, 'events', '2022-09-18', 'تقديم خدمات طبيه بالتعاون مع اندلسية', NULL, NULL, NULL, '<p>يسر جمعية دعم لرعاية المطلقات والارامل وابنائهن&nbsp;<br>بالتعاون مع أندلسيه للخدمات الطبيه&nbsp;<br>مشاركتنا الاحتفال باليوم الوطني&nbsp;<br>وتقديم الخدمات لمستفيداتها &nbsp;من خلال طبيبة أسنان (كشف على الاسنان مجاناً ) وتقديم بطاقات خصم ٥٠ ٪؜ على المعالجات&nbsp;<br>وطبيبة عام &nbsp;للكشف على المستفيدات&nbsp;<br>&nbsp;<br>&nbsp;هدايا مقدمة من أندلسيه للخدمات الطبيه&nbsp;<br>مسابقات وجائزة &nbsp;خدمة مجاناً مقدمة من قسم الجلديه&nbsp;</p><p>بمقر الجمعيه بحي المحمدية<br>يوم الثلاثاء ٢٠ /٩/ ٢٠٢٢م&nbsp;<br>من الساعه ٩ صباحاً الى الساعه ١ مساءً</p>', 0, 1, 0, '2023-09-15 08:09:09', '2023-09-15 08:09:09', NULL),
(43, 'news', '2022-09-21', 'اجتماع الجمعية العمومية 2022', NULL, NULL, NULL, '<p>اجتماع الجمعية العمومية 2022</p><p>‏تم بحمد الله&nbsp; الانتهاء من عقد اجتماع الجمعيه العموميه العادية برئاسة الأستاذة نورا آل الشيخ رئيسة مجلس الإدارة وأعضاء الجمعية العمومية تم خلاله عرض ومناقشة جوانب عده وتمت الموافقه بالاجماع على المحاور الخاصة بجدول الأعمال</p>', 0, 0, 0, '2023-09-15 08:10:23', '2023-10-09 06:14:20', NULL),
(44, 'news', '2022-09-10', 'توقيع اتفاقية ادارة التطوع', NULL, NULL, NULL, '<p>بحمد الله&nbsp;<br>تم توقيع اتفاقية ادارة التطوع بجمعية دعم مع مجموعة #نبض جدة التطوعية &nbsp;التابعة لـ#جمعية نفع الخيرية&nbsp;<br>نسأل الله لنا ولهم التوفيق</p>', 0, 1, 0, '2023-09-15 08:11:25', '2023-09-15 08:11:25', NULL),
(45, 'events', '2022-11-28', 'حضور جائزة الاميرة صيته للعمل الاجتماعى', NULL, NULL, NULL, '<p>بدعوة كريمة من مؤسسة جائزة الاميرة صيته للعمل الاجتماعي شاركت رئيسة مجلس ادارة جمعيه دعم<br>بحضور انطلاقه ملتقى /درايه&nbsp;<br>برعاية صاحب السمو الامير سعود بن فهد بن عبد الله الكبير<br>في نسخته الاولي بفندق الفيرمان&nbsp;<br>حيث تم شرح اهداف ملتقى دراية لحياة أكثر امنآ وحماية والذى جاء بعنوان ماذا لو .. كيف تتصرف؟ &nbsp;‏ويركز مسار البراعم على كيفية حماية الأطفال من التحرش والتنمر والابتزاز واستخدام الأجهزة الالكترونية ومساعدتهم على استخدام العادات الغذائية الصحية، ومعرفة حقوقهم واحترام الآخرين لها، والتعريف بالتطبيقات والخدمات الالكترونية في مسار البراعم<br>&nbsp;</p>', 0, 1, 0, '2023-09-15 08:12:50', '2023-09-15 08:12:50', NULL),
(46, 'news', '2022-11-12', 'تعاون مثمر مع ترميم للتنمية', NULL, NULL, NULL, '<p>تم بفضل الله الانتهاء من تجهيز مبنى سكنى لمستفيدات جمعية دعم لرعاية الأرامل والمطلقات ومقره جده وذلك بالتعاون المثمر مع ترميم للتنمية فى إطار الشراكة بين الطرفين لتوفير السكن لمستفيدات الجمعية</p>', 0, 0, 0, '2023-09-15 08:13:51', '2023-10-11 12:52:40', NULL),
