(21, 'مشروع فريق الصيانة التطوعى', NULL, 6.00, 0.00, 'مشروع فريق الصيانة التطوعى', '<p>للمساهمة فى صيانة منازل الايتام &nbsp;انضم الى مشروع فريق الصيانة التطوعى</p>', 1, 1, 1, '2023-04-09 20:59:33', '2023-11-21 03:04:02', NULL),
(22, 'مشروع انامل صانعة', NULL, 0.00, 0.00, 'مشروع انامل صانعة', '<p>مشروع انامل صانعة ومكن ارملة او مطلقة علي تعلم حرفة&nbsp;</p>', 1, 1, 1, '2023-09-27 16:18:39', '2023-11-21 03:03:11', NULL),
