INSERT INTO `posts` (`id`, `type`, `date`, `title`, `writer`, `short_description`, `video_link`, `description`, `head_line`, `published`, `featured`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'events', '2023-08-03', 'إنطلاق الدورة التدريبية بعنوان (فنون الخياطة)', NULL, NULL, NULL, '<p>‏ولله الحمد تم بالأمس إنطلاق الدورة التدريبية بعنوان (فنون الخياطة) لمشروع أنامل دعم الممنوح من برنامج أهالينا للاستثمار الاجتماعي لمستفيدي جمعية دعم تحت رعاية من مؤسسة الملك خالد</p>', 0, 0, 0, '2023-08-03 10:58:12', '2023-10-11 12:53:03', NULL),
(2, 'tkremat', '2023-04-11', 'تكريم جمعية دعم في حفل المشاركين بأعمال التسكين', NULL, 'تكريم جمعية دعم في حفل المشاركين بأعمال التسكين', NULL, '<p>&nbsp;تكريم جمعية دعم في حفل المشاركين بأعمال التسكين</p><p>&nbsp;‏قام صاحب السمو الملكي الامير بدر بن سلطان بن عبدالعزيز نائب أمير منطقة مكة المكرمة ليلة البارحه في مقر &nbsp;الامارة بجده وبحضور معالي نائب وزير الاسكان ومعالي امين محافظة جدة بتكريم الجمعيات المشاركة بالاسكان بالتعاون مع وزارة الاسكان ومنهم جمعية دعم لرعاية الارامل والمطلقات بمكة</p>', 0, 1, 0, '2023-09-12 02:49:02', '2023-09-15 05:55:13', NULL),
(3, 'news', '2023-09-11', 'مبادرة انشر الخير', NULL, NULL, NULL, '<p>تشرفت الجمعية بزيارة المدير التنفيذي للمنصة الوطنية للتبرعات \"تبرع\" أ. صالح العمري لتقديم الدعم من⁩ ⁧<a href=\"https://twitter.com/hashtag/%D9%85%D8%A7%D9%83%D8%AF%D9%88%D9%86%D8%A7%D9%84%D8%AF%D8%B2_%D8%A7%D9%84%D8%B3%D8%B9%D9%88%D8%AF%D9%8A%D8%A9?src=hashtag_click\">#ماكدونالدز_السعودية</a>⁩ وهي اجهزة كهربائية ضمن مبادرة انشر الخير</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:35:59', '2023-09-17 01:24:53', NULL),
(4, 'news', '2023-09-11', 'مشروع الكسب الكريم', NULL, NULL, NULL, '<p>انطلاق مشروع الكسب الكريم في اول ايام التدريب على مهارات سوق العمل والمنتهي بالتوظيف مع الجهات الشريكة والمدعوم من مؤسسة عبدالعزيز الجميح</p><p><br>&nbsp;</p>', 1, 1, 1, '2023-09-15 05:38:23', '2023-09-15 05:55:01', NULL),
(5, 'news', '2023-08-31', 'زياره مؤسسة غروس', NULL, NULL, NULL, '<p>تشرفت جمعية <a href=\"https://twitter.com/hashtag/%D8%AF%D8%B9%D9%85?src=hashtag_click\">#دعم</a> بزيارة ممثل مؤسسة محمد ابراهيم السبيعي <a href=\"https://twitter.com/hashtag/%D8%BA%D8%B1%D9%88%D8%B3?src=hashtag_click\">#غروس</a> الأستاذة / نوال المشيبه، وذلك للاطلاع على برامج الجمعية ومبادراتها، وبحث سبل التعاون بين الجهتين</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:40:05', '2023-09-17 01:24:59', NULL),
(6, 'news', '2023-08-31', 'زيارة جمعية ترميم للتنمية', NULL, NULL, NULL, '<p>استضافة جمعية <a href=\"https://twitter.com/hashtag/%D8%AF%D8%B9%D9%85_%D9%84%D8%B1%D8%B9%D8%A7%D9%8A%D8%A9_%D8%A7%D9%84%D9%85%D8%B7%D9%84%D9%82%D8%A7%D8%AA_%D9%88%D8%A7%D9%84%D8%A7%D8%B1%D8%A7%D9%85%D9%84_%D9%88%D8%A7%D8%A8%D9%86%D8%A7%D8%A6%D9%87%D9%86?src=hashtag_click\">#دعم_لرعاية_المطلقات_والارامل_وابنائهن</a> جمعية <a href=\"https://twitter.com/hashtag/%D8%AA%D8%B1%D9%85%D9%8A%D9%85_%D9%84%D9%84%D8%AA%D9%86%D9%85%D9%8A%D8%A9?src=hashtag_click\">#ترميم_للتنمية</a>⁩ وقدمت أ/ وفاء العزب المديرة التنفيذية لجمعية دعم شكرها لإدارة وفريق ترميم على جهودهم لإنجاح الشراكة بما يعود بالنفع على المستفيدات من الأرامل والمطلقات وابنائهن.</p><p><br>&nbsp;</p>', 0, 1, 1, '2023-09-15 05:41:35', '2023-09-17 01:25:04', NULL),
